Ôªøimport React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  Grid,
  Chip,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  Divider,
} from "@mui/material";
import {
  DatePicker,
  TimePicker,
  LocalizationProvider,
} from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { vi } from "date-fns/locale";
import {
  AccessTime,
  CalendarToday,
  Schedule,
  Warning,
  CheckCircle,
} from "@mui/icons-material";
import {
  addDays,
  format,
  isAfter,
  isBefore,
  setHours,
  setMinutes,
  startOfDay,
} from "date-fns";

const ChargingDateTimePicker = ({
  onDateTimeChange,
  station = null,
  initialDateTime = null,
  disabled = false,
}) => {
  const [selectedDate, setSelectedDate] = useState(
    initialDateTime?.date || null
  );
  const [selectedTime, setSelectedTime] = useState(
    initialDateTime?.time || null
  );
  const [schedulingType] = useState("scheduled"); // Always scheduled, removed immediate option
  const [errors, setErrors] = useState({});
  const [availableSlots, setAvailableSlots] = useState([]);

  // Common time slots
  const commonTimeSlots = [
    "07:00",
    "07:30",
    "08:00",
    "08:30",
    "09:00",
    "09:30",
    "10:00",
    "10:30",
    "11:00",
    "11:30",
    "12:00",
    "12:30",
    "13:00",
    "13:30",
    "14:00",
    "14:30",
    "15:00",
    "15:30",
    "16:00",
    "16:30",
    "17:00",
    "17:30",
    "18:00",
    "18:30",
    "19:00",
    "19:30",
    "20:00",
    "20:30",
    "21:00",
    "21:30",
  ];

  useEffect(() => {
    validateDateTime();
    updateAvailableSlots();
  }, [selectedDate, selectedTime, schedulingType]);

  useEffect(() => {
    // Notify parent component when date/time changes
    if (onDateTimeChange) {
      onDateTimeChange({
        schedulingType,
        scheduledDate: selectedDate,
        scheduledTime: selectedTime,
        scheduledDateTime:
          selectedDate && selectedTime
            ? new Date(
                selectedDate.getFullYear(),
                selectedDate.getMonth(),
                selectedDate.getDate(),
                selectedTime.getHours(),
                selectedTime.getMinutes()
              )
            : null,
        isValid: Object.keys(errors).length === 0,
      });
    }
     
  }, [selectedDate, selectedTime, schedulingType, errors]);

  const validateDateTime = () => {
    const newErrors = {};
    const now = new Date();

    // Always require date and time since "S·∫°c ngay" option is removed
    // Validate date
    if (!selectedDate) {
      newErrors.date = "Vui l√≤ng ch·ªçn ng√†y s·∫°c";
    } else {
      const today = startOfDay(now);
      const selectedDay = startOfDay(selectedDate);
      const maxDate = addDays(today, 7); // Allow booking up to 7 days ahead

      if (isBefore(selectedDay, today)) {
        newErrors.date = "Kh√¥ng th·ªÉ ch·ªçn ng√†y trong qu√° kh·ª©";
      } else if (isAfter(selectedDay, maxDate)) {
        newErrors.date = "Ch·ªâ c√≥ th·ªÉ ƒë·∫∑t l·ªãch t·ªëi ƒëa 7 ng√†y tr∆∞·ªõc";
      }
    }

    // Validate time
    if (!selectedTime) {
      newErrors.time = "Vui l√≤ng ch·ªçn gi·ªù s·∫°c";
    } else if (selectedDate) {
      const selectedDateTime = new Date(
        selectedDate.getFullYear(),
        selectedDate.getMonth(),
        selectedDate.getDate(),
        selectedTime.getHours(),
        selectedTime.getMinutes()
      );

      // Check if selected time is in the past (for today only)
      if (format(selectedDate, "yyyy-MM-dd") === format(now, "yyyy-MM-dd")) {
        const oneHourLater = new Date(now.getTime() + 60 * 60 * 1000); // 1 hour from now
        if (isBefore(selectedDateTime, oneHourLater)) {
          newErrors.time = "Vui l√≤ng ch·ªçn th·ªùi gian √≠t nh·∫•t 1 gi·ªù sau hi·ªán t·∫°i";
        }
      }

      // Check station operating hours
      if (station?.operatingHours && station.operatingHours !== "24/7") {
        const openTime = station.operatingHours.open;
        const closeTime = station.operatingHours.close;
        const selectedHour = format(selectedTime, "HH:mm");

        if (selectedHour < openTime || selectedHour > closeTime) {
          newErrors.time = `Tr·∫°m s·∫°c ch·ªâ ho·∫°t ƒë·ªông t·ª´ ${openTime} - ${closeTime}`;
        }
      }
    }

    setErrors(newErrors);
  };

  const updateAvailableSlots = () => {
    if (!selectedDate) {
      setAvailableSlots([]);
      return;
    }

    // Mock available slots (in real app, this would come from API)
    const mockOccupiedSlots = ["09:00", "14:00", "18:30"]; // Some slots are taken
    const available = commonTimeSlots.filter(
      (slot) => !mockOccupiedSlots.includes(slot)
    );
    setAvailableSlots(available);
  };

  // scheduling type is fixed to "scheduled" in this component

  const handleQuickDateSelect = (days) => {
    const newDate = addDays(new Date(), days);
    setSelectedDate(newDate);
  };

  const handleQuickTimeSelect = (timeStr) => {
    const [hours, minutes] = timeStr.split(":").map(Number);
    const timeDate = setMinutes(setHours(new Date(), hours), minutes);
    setSelectedTime(timeDate);
  };

  const getDateHelperText = () => {
    if (errors.date) return errors.date;
    if (selectedDate) {
      const today = startOfDay(new Date());
      const selected = startOfDay(selectedDate);
      if (selected.getTime() === today.getTime()) return "H√¥m nay";
      if (selected.getTime() === addDays(today, 1).getTime()) return "Ng√†y mai";
      return format(selectedDate, "EEEE, dd/MM/yyyy", { locale: vi });
    }
    return "Ch·ªçn ng√†y b·∫°n mu·ªën s·∫°c";
  };

  const getTimeHelperText = () => {
    if (errors.time) return errors.time;
    if (selectedTime) {
      return `Th·ªùi gian ƒë√£ ch·ªçn: ${format(selectedTime, "HH:mm")}`;
    }
    return "Ch·ªçn gi·ªù b·∫Øt ƒë·∫ßu s·∫°c";
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} adapterLocale={vi}>
      <Card elevation={0} sx={{ border: "1px solid #e0e0e0" }}>
        <CardContent>
          <Typography
            variant="h6"
            gutterBottom
            sx={{ display: "flex", alignItems: "center", gap: 1 }}
          >
            <Schedule color="primary" />
            L√™n l·ªãch s·∫°c
          </Typography>

          {/* Scheduled DateTime Selection */}
          <Box>
            {/* Quick Date Selection */}
            <Typography variant="subtitle2" gutterBottom>
              Ch·ªçn nhanh:
            </Typography>
            <Box sx={{ display: "flex", gap: 1, mb: 2, flexWrap: "wrap" }}>
              <Chip
                label="H√¥m nay"
                onClick={() => handleQuickDateSelect(0)}
                variant={
                  selectedDate &&
                  format(selectedDate, "yyyy-MM-dd") ===
                    format(new Date(), "yyyy-MM-dd")
                    ? "filled"
                    : "outlined"
                }
                color="primary"
                disabled={disabled}
              />
              <Chip
                label="Ng√†y mai"
                onClick={() => handleQuickDateSelect(1)}
                variant={
                  selectedDate &&
                  format(selectedDate, "yyyy-MM-dd") ===
                    format(addDays(new Date(), 1), "yyyy-MM-dd")
                    ? "filled"
                    : "outlined"
                }
                color="primary"
                disabled={disabled}
              />
              
            </Box>

            {/* Date Picker */}
            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12} sm={6}>
                <DatePicker
                  label="Ch·ªçn ng√†y"
                  value={selectedDate}
                  onChange={(newDate) => setSelectedDate(newDate)}
                  disabled={disabled}
                  minDate={new Date()}
                  maxDate={addDays(new Date(), 7)}
                  slotProps={{
                    textField: {
                      fullWidth: true,
                      error: !!errors.date,
                      helperText: getDateHelperText(),
                      InputProps: {
                        startAdornment: (
                          <CalendarToday
                            sx={{ mr: 1, color: "action.active" }}
                          />
                        ),
                      },
                    },
                  }}
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TimePicker
                  label="Ch·ªçn gi·ªù"
                  value={selectedTime}
                  onChange={(newTime) => setSelectedTime(newTime)}
                  disabled={disabled || !selectedDate}
                  ampm={false}
                  slotProps={{
                    textField: {
                      fullWidth: true,
                      error: !!errors.time,
                      helperText: getTimeHelperText(),
                      InputProps: {
                        startAdornment: (
                          <AccessTime sx={{ mr: 1, color: "action.active" }} />
                        ),
                      },
                    },
                  }}
                />
              </Grid>
            </Grid>

            {/* Quick Time Selection */}
            {selectedDate && (
              <Box>
                <Typography variant="subtitle2" gutterBottom>
                  Khung gi·ªù ph·ªï bi·∫øn:
                </Typography>
                <Box sx={{ display: "flex", gap: 1, mb: 2, flexWrap: "wrap" }}>
                  {["07:00", "09:00", "12:00", "15:00", "18:00", "20:00"].map(
                    (time) => (
                      <Chip
                        key={time}
                        label={time}
                        onClick={() => handleQuickTimeSelect(time)}
                        variant={
                          selectedTime && format(selectedTime, "HH:mm") === time
                            ? "filled"
                            : "outlined"
                        }
                        color={
                          availableSlots.includes(time) ? "primary" : "default"
                        }
                        disabled={disabled || !availableSlots.includes(time)}
                      />
                    )
                  )}
                </Box>
              </Box>
            )}

            {/* Station Operating Hours Info */}
            {station?.operatingHours && (
              <Alert severity="info" sx={{ mb: 2 }} icon={<AccessTime />}>
                <Typography variant="body2">
                  <strong>Gi·ªù ho·∫°t ƒë·ªông c·ªßa tr·∫°m:</strong>{" "}
                  {station.operatingHours === "24/7"
                    ? "24/7"
                    : `${station.operatingHours.open} - ${station.operatingHours.close}`}
                </Typography>
              </Alert>
            )}

            {/* Validation Messages */}
            {Object.keys(errors).length > 0 && (
              <Alert severity="warning" sx={{ mb: 2 }} icon={<Warning />}>
                <Typography variant="body2">
                  Vui l√≤ng ki·ªÉm tra l·∫°i th√¥ng tin ƒë√£ ch·ªçn
                </Typography>
              </Alert>
            )}
          </Box>

          {/* Summary */}
          {selectedDate && selectedTime && Object.keys(errors).length === 0 && (
            <Paper
              elevation={1}
              sx={{ p: 2, bgcolor: "#f8f9fa", border: "1px solid #e3f2fd" }}
            >
              <Typography variant="subtitle2" color="primary" gutterBottom>
                üìÖ T√≥m t·∫Øt l·ªãch s·∫°c
              </Typography>
              <Typography variant="body2">
                <strong>Ng√†y:</strong>{" "}
                {format(selectedDate, "EEEE, dd/MM/yyyy", { locale: vi })}
              </Typography>
              <Typography variant="body2">
                <strong>Gi·ªù:</strong> {format(selectedTime, "HH:mm")}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Vui l√≤ng c√≥ m·∫∑t t·∫°i tr·∫°m s·∫°c tr∆∞·ªõc 15 ph√∫t
              </Typography>
            </Paper>
          )}
        </CardContent>
      </Card>
    </LocalizationProvider>
  );
};

export default ChargingDateTimePicker;

Ôªøimport React, { useState, useEffect } from 'react';
import { QrReader } from 'react-qr-reader';
import useStationStore from '../../../store/stationStore';
import useBookingStore from '../../../store/bookingStore';
import './QRCodeScanner.css';

const QRCodeScanner = ({ onScanSuccess, onClose }) => {
  const [scanning, setScanning] = useState(true);
  const [error, setError] = useState(null);
  const [hasPermission, setHasPermission] = useState(null);
  
  const { getStationById } = useStationStore();
  const { createBooking } = useBookingStore();

  // Request camera permission on component mount
  useEffect(() => {
    const requestCameraPermission = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        setHasPermission(true);
        // Stop the stream immediately as QrReader will handle it
        stream.getTracks().forEach(track => track.stop());
      } catch (err) {
        setHasPermission(false);
        setError('Kh√¥ng th·ªÉ truy c·∫≠p camera. Vui l√≤ng cho ph√©p truy c·∫≠p camera.');
      }
    };

    requestCameraPermission();
  }, []);

  const handleScan = async (result, error) => {
    if (result) {
      try {
        const qrData = result?.text || result;
        console.log('QR Code scanned:', qrData);
        
        // Parse QR data (expected format: STATION:ST001:PORT:A01 or JSON)
        let stationId, portId;
        try {
          const parsed = JSON.parse(qrData);
          stationId = parsed.stationId;
          portId = parsed.portId;
        } catch {
          const parts = qrData.split(':');
          stationId = parts[1];
          portId = parts[3];
        }

        if (!stationId || !portId) {
          throw new Error('M√£ QR kh√¥ng h·ª£p l·ªá');
        }

        // Get station details
        const station = getStationById(stationId);
        if (!station) {
          throw new Error('Kh√¥ng t√¨m th·∫•y tr·∫°m s·∫°c');
        }
          
          // Create booking via API
          const bookingResult = await createBooking({
            stationId: station.id,
            stationName: station.name,
            chargerType: {
              id: 'auto',
              name: 'Auto-selected',
              power: `${station.charging.maxPower} kW`,
              price: `${station.charging.pricing.dcRate || station.charging.pricing.acRate} VNƒê/kWh`,
            },
            connector: {
              id: 'auto',
              name: 'Auto-detected',
              compatible: 'Universal',
            },
            port: {
              id: portId,
              location: `Port ${portId}`,
            },
            bookingTime: new Date().toISOString(),
            scannedAt: new Date().toISOString(),
            autoStart: true,
            schedulingType: 'qr_immediate', // Special case: QR scan for immediate charging (not pre-scheduled)
            source: 'qr_scan'
          });

          if (bookingResult.success && onScanSuccess) {
            onScanSuccess({
              station,
              booking: bookingResult.booking,
              portId,
              message: 'ƒê√£ qu√©t m√£ QR th√†nh c√¥ng'
            });
          } else if (!bookingResult.success) {
            throw new Error(bookingResult.error || 'Kh√¥ng th·ªÉ t·∫°o booking');
          }
      } catch (err) {
        console.error('QR Scan Error:', err);
        setError(err.message || 'Kh√¥ng th·ªÉ x·ª≠ l√Ω m√£ QR. Vui l√≤ng th·ª≠ l·∫°i.');
      }
      setScanning(false);
    }

    if (error) {
      console.warn('QR Scanner error:', error);
    }
  };

  // helper removed: handleStationFound was unused and duplicated booking logic

  const handleRetry = () => {
    setError(null);
    setScanning(true);
  };

  if (hasPermission === false) {
    return (
      <div className="qr-scanner-container">
        <div className="qr-scanner-modal">
          <div className="qr-scanner-header">
            <h3>Qu√©t m√£ QR tr·∫°m s·∫°c</h3>
            <button className="close-btn" onClick={onClose}>√ó</button>
          </div>
          <div className="qr-scanner-error">
            <div className="error-icon">üì∑</div>
            <p>Kh√¥ng th·ªÉ truy c·∫≠p camera</p>
            <p className="error-detail">
              Vui l√≤ng cho ph√©p truy c·∫≠p camera trong tr√¨nh duy·ªát ƒë·ªÉ s·ª≠ d·ª•ng t√≠nh nƒÉng qu√©t QR.
            </p>
            <button className="retry-btn" onClick={handleRetry}>
              Th·ª≠ l·∫°i
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="qr-scanner-container">
      <div className="qr-scanner-modal">
        <div className="qr-scanner-header">
          <h3>Qu√©t m√£ QR tr·∫°m s·∫°c</h3>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>
        
        <div className="qr-scanner-content">
          {scanning && hasPermission && !error && (
            <div className="scanner-wrapper">
              <QrReader
                onResult={handleScan}
                style={{ width: '100%' }}
                constraints={{
                  video: {
                    facingMode: 'environment' // Use back camera
                  }
                }}
              />
              <div className="scanner-overlay">
                <div className="scanner-frame">
                  <div className="corner top-left"></div>
                  <div className="corner top-right"></div>
                  <div className="corner bottom-left"></div>
                  <div className="corner bottom-right"></div>
                </div>
                <p className="scanner-instruction">
                  H∆∞·ªõng camera v√†o m√£ QR tr√™n tr·∫°m s·∫°c
                </p>
              </div>
            </div>
          )}
          
          {error && (
            <div className="qr-scanner-error">
              <div className="error-icon">‚ö†Ô∏è</div>
              <p>{error}</p>
              <button className="retry-btn" onClick={handleRetry}>
                Qu√©t l·∫°i
              </button>
            </div>
          )}
        </div>
        
        <div className="qr-scanner-footer">
          <p className="scanner-help">
            üí° M·∫πo: ƒê·∫£m b·∫£o m√£ QR trong khung v√† c√≥ ƒë·ªß √°nh s√°ng
          </p>
        </div>
      </div>
    </div>
  );
};

export default QRCodeScanner;

import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import staffAPI from "../../services/api/staffAPI";
import {
  Box,
  Grid,
  Typography,
  Card,
  CardContent,
  Button,
  Alert,
  AlertTitle,
  Chip,
  Divider,
  Stack,
} from "@mui/material";
import {
  ElectricCar,
  BatteryChargingFull,
  Warning,
  CheckCircle,
  Build,
  PowerOff,
  Refresh,
  Notifications,
  Error,
  Bolt,
  AccessTime,
  MonetizationOn,
} from "@mui/icons-material";

const StaffDashboard = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [stationInfo, setStationInfo] = useState(null);
  const [connectors, setConnectors] = useState([]);
  const [dailyStats, setDailyStats] = useState({
    revenue: 0,
    completedSessions: 0,
    energyConsumed: 0,
  });
  const [alerts, setAlerts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await staffAPI.getDashboardOverview();

      if (!response || typeof response !== "object") {
        throw new Error("Kh√¥ng nh·∫≠n ƒë∆∞·ª£c d·ªØ li·ªáu dashboard");
      }

      const {
        hasAssignment,
        station,
        staff,
        connectors: connectorPayload = [],
        dailyStats: dailyStatsPayload,
        alerts: alertPayload = [],
      } = response;

      if (hasAssignment && station) {
        setStationInfo({
          id: station.stationId,
          name: station.stationName,
          address: `${station.address}${station.city ? `, ${station.city}` : ""}`,
          staffName: staff?.fullName || "",
        });
      } else {
        setStationInfo(null);
      }

      const normalizedConnectors = Array.isArray(connectorPayload)
        ? connectorPayload
            .map((connector) => mapConnectorForDisplay(connector))
            .filter(Boolean)
        : [];
      setConnectors(normalizedConnectors);

      if (dailyStatsPayload) {
        setDailyStats({
          revenue: Number(dailyStatsPayload.revenue || 0),
          completedSessions: Number(dailyStatsPayload.completedSessions || 0),
          energyConsumed: Number(dailyStatsPayload.energyDeliveredKwh || 0),
        });
      } else {
        setDailyStats({ revenue: 0, completedSessions: 0, energyConsumed: 0 });
      }

      const normalizedAlerts = Array.isArray(alertPayload)
        ? alertPayload.map((alert) => ({
            id: alert.alertId ?? safeRandomId(),
            type: normalizeAlertSeverity(alert.severity),
            message: alert.message,
            timestamp: alert.createdAtUtc ? new Date(alert.createdAtUtc) : new Date(),
          }))
        : [];
      setAlerts(normalizedAlerts);
    } catch (error) {
      console.error("Error loading dashboard:", error);
      setError(error.message || "Kh√¥ng th·ªÉ t·∫£i dashboard nh√¢n vi√™n");
      setStationInfo(null);
      setConnectors([]);
      setDailyStats({ revenue: 0, completedSessions: 0, energyConsumed: 0 });
      setAlerts([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case "Available":
        return <CheckCircle color="success" />;
      case "Charging":
        return <BatteryChargingFull color="primary" />;
      case "Faulted":
        return <Warning color="error" />;
      case "Unavailable":
        return <PowerOff color="disabled" />;
      default:
        return <Build color="warning" />;
    }
  };

  const safeRandomId = () =>
    typeof crypto !== "undefined" && crypto.randomUUID
      ? crypto.randomUUID()
      : `alert-${Date.now()}-${Math.random().toString(16).slice(2)}`;

  const normalizeAlertSeverity = (severity) => {
    const normalized = (severity || "info").toString().toLowerCase();
    if (normalized === "error" || normalized === "critical") return "error";
    if (normalized === "warning" || normalized === "warn") return "warning";
    return "info";
  };

  const mapConnectorForDisplay = (connector) => {
    if (!connector) return null;

    const rawStatus = (connector.operationalStatus || connector.technicalStatus || "").trim();
    const statusKey = rawStatus.toLowerCase();

    const statusMap = {
      available: { status: "Available", label: "R·∫£nh", color: "success" },
      charging: { status: "Charging", label: "ƒêang s·∫°c", color: "primary" },
      in_use: { status: "Charging", label: "ƒêang s·∫°c", color: "primary" },
      maintenance: { status: "Faulted", label: "B·∫£o tr√¨", color: "warning" },
      faulted: { status: "Faulted", label: "L·ªói", color: "error" },
      offline: { status: "Faulted", label: "Offline", color: "error" },
      unavailable: { status: "Unavailable", label: "Kh√¥ng kh·∫£ d·ª•ng", color: "default" },
      reserved: { status: "Reserved", label: "ƒê√£ gi·ªØ ch·ªó", color: "info" },
    };

    const mapped = statusMap[statusKey] || {
      status: "Unknown",
      label: rawStatus || "Kh√¥ng x√°c ƒë·ªãnh",
      color: "default",
    };

    let currentSession = null;
    if (connector.activeSession) {
      const session = connector.activeSession;
      currentSession = {
        id: `SES-${session.bookingId}`,
        startTime: session.startedAt ? new Date(session.startedAt) : null,
        energyConsumed: Number(session.energyDelivered || 0),
        vehicleSOC:
          session.currentSoc !== undefined && session.currentSoc !== null
